mapper.update:
declare begin
insert into app_sari_nation_t( nation, qty_type, qty, creation_date ) values ( #{it.nation,jdbcType=VARCHAR}, #{it.qtyType,jdbcType=VARCHAR}, #{it.qty,jdbcType=INTEGER}, trunc(#{it.creationDate,jdbcType=DATE}) );
commit; end;

mapper.select:
select distinct nation "id", nation "text" from app_sari_nation_t where 1=0
or nation=#{nation,jdbcType=VARCHAR}
order by nlssort(city,'NLS_SORT=SCHINESE_PINYIN_M')
